import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { ErrormessageComponent } from '../errormessage/errormessage.component';
import { UserLog } from '../shared/user.model';
import { UserService } from '../shared/user.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  public login: UserLog;
  public loginUser: any;
  public loginForm: FormGroup;
  public returnUrl: string;
  private message:string;
  public constructor(private userService: UserService,
    public readonly router: Router,
    private formBuilder: FormBuilder) { }

  public ngOnInit(): void {
    this.loginForm = this.formBuilder.group({
      Email: ['', Validators.required],
      Password: ['', Validators.required]
    });
    this.returnUrl = '/home';
    this.userService.logout();
    this.resetForm();
  }
  get f() { return this.loginForm.controls; }
  Login() {  

    // stop here if form is invalid  
    if (this.loginForm.invalid) {  
       return;  
    }  
    else {  
       if (this.f.Email.value == this.login.Email && this.f.Password.value == this.login.Password) {  
          console.log("Login successful");  
          //this.authService.authLogin(this.model);  
          localStorage.setItem('isLoggedIn', "true");  
          this.router.navigate([this.returnUrl]);  
       }  
    else {  
       this.message = "Please check your userid and password";  
       }  
      }  
    }

  public resetForm(form?: NgForm) {
    if (form != null)
      form.reset();
    this.login = {
      Email: '',
      Password: '',
    }
  }

  public async OnSubmit(form: NgForm): Promise<void> {
    this.userService.loginUser(form.value)
      .subscribe(() => {
        // sessionStorage.setItem('loggedUser', this.login.Email);
        //this.userService.loginUser(this.loginUser.Email);
        this.router.navigateByUrl('home');
        this.resetForm(form);
      });
  }
}

