<html lang="en">
<head>
  <meta charset="utf-8">
  <title>Login!</title>
  <link rel="stylesheet" href="../login/login.component.css">
  <script>
      function welcome(){
          var x = document.getElementById("FirstName").nodeValue;
      }
  </script>
</head>
<body>
<img src="../pickawood-gf8e6XvG_3E-unsplash.jpg" class="img">
    <div class="center">
    <h1>Login</h1>
    <form method="post" class="col s12 white" #log="ngForm" (ngSubmit)="OnSubmit(log)">
      <div class="txt_field">
        <input  class="validate" type="text" name="Email" #Email="ngModel" [(ngModel)]="login.Email" required  minlength="3">
        <span></span>
        <label>Email</label>
      </div>
      <div class="txt_field">
        <input type="password" class="validate" type="password" name="Password" #Password="ngModel" [(ngModel)]="login.Password" required  minlength="3">
        <span></span>
        <label>Password</label>
      </div>
      <div class="pass" routerLink="/forget">Forgot Password?</div>
      <input type="submit" value="Login" onclick="return welcome()">
      <div class="signup_link">
        Not a member? <a routerLink="/signup">Signup</a><br /><a routerLink="/home" >Continue Without Signing-Up</a>
        <br /><a routerLink="/addproduct">Add Product</a>
      </div>

    </form>
  </div>

</body>
</html>
