import { Component, OnInit } from '@angular/core';
import { UserService } from '../shared/user.service';
import { Router } from '@angular/router';
import { AddItem, CartItem } from '../shared/user.model';
import { NgForm } from '@angular/forms';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.css']
})
export class HomePageComponent implements OnInit {
  public size: string="S";
    public addToCart(product: CartItem){
    this.userService.addToCart(product,this.size);
    window.alert('product added');

  }

  public isLoggedIn$: Observable<boolean>;
  constructor(public userService: UserService,public readonly router: Router) { }

  ngOnInit(): void {
    this.userService.getdetails();
    this.userService.getImage();
  }
  changeSize(event: any){
    this.size=event.target.value;
  }

  public async Click(button: NgForm): Promise<void> {
    // this.userService.addToCart(button.value)
    // .subscribe(()=>{
    // });
  }

}


<link rel="stylesheet" href="../home-page/home-page.component.css">
<!-- <section id="about"> -->
<div class="container">
  <div class="row">
    <div class="col-md-3" *ngFor="let product of userService.list ">
      <div class="card mb-3" *ngIf="product">
        <img src="{{'data:image/jpg;base64,' + product.image}}" class="card-img-top">
        <div class="card-body"><span>
            <h3 class="card-text"><small class="text-muted">{{product.productName}}</small></h3>
            <p class="card-title">Description:{{product.productDescription}}</p>
            <p class="card-price">Price : ₹{{product.price}}</p>
            <p class="card-size">Size : {{product.size}}</p>
            <button type="button" type="submit" class="btn btn-info" (click)="addToCart(product)">Add to
              Cart</button><button type="button" class="btn btn-success" (click)="addToCart(product)">Buy Now</button>
          </span>
        </div>
      </div>
    </div>
  </div>
</div>
<nav aria-label="Page navigation example">
  <ul class="pagination justify-content-center">
    <li class="page-item disabled">
      <a class="page-link" href="#" tabindex="-1">Previous</a>
    </li>
    <li class="page-item"><a class="page-link" routerLink="/home">1</a></li>
    <li class="page-item"><a class="page-link" routerLink="/home">2</a></li>
    <li class="page-item"><a class="page-link" routerLink="/home">3</a></li>
    <li class="page-item">
      <a class="page-link" href="#">Next</a>
    </li>
  </ul>
</nav>
